import { reactive } from 'vue';

export const EventBus = reactive({
  successMessage: '',
  errorMessage: '',
  setMessage(message, type = 'success') {
    if (type === 'success') {
      this.successMessage = message;
      setTimeout(() => {
        this.successMessage = '';
      }, 5000); // Clear message after 5 seconds
    } else {
      this.errorMessage = message;
      setTimeout(() => {
        this.errorMessage = '';
      }, 5000);
    }
  }
});
